mapper.select:
select

from tenants_service_items
where TENANT_ID = #{tenantId,jdbcType=INTEGER}
  and SERVICE_ITEM_CODE = #{serviceItemCode,jdbcType=VARCHAR}

mapper.delete:
delete from tenants_service_items
where TENANT_ID = #{tenantId,jdbcType=INTEGER}
  and SERVICE_ITEM_CODE = #{serviceItemCode,jdbcType=VARCHAR}
